<script>
    import { onMount, onDestroy, tick } from 'svelte';

    export let actual_speed;

    console.log("Actual speed:", actual_speed);
    
    function calculateStrokeDashoffset(speed) {
        // The maximum stroke-dashoffset is 615, which corresponds to a speed of 0 km/h
        // The minimum stroke-dashoffset is 0, which corresponds to a speed of 180 km/h
        return 615 - ((speed / 400) * 615);
    }

    const keyframes = `
    @keyframes dash {
      100% {
        stroke-dashoffset: ${calculateStrokeDashoffset(actual_speed)};
      }
        0% {
        stroke-dashoffset: 615;
      }
    }
    
    @keyframes dash2 {
      100% {
        stroke-dashoffset: ${calculateStrokeDashoffset(actual_speed)+5};
      }
        0% {
        stroke-dashoffset: ${calculateStrokeDashoffset(actual_speed)-5};
      }
    }
  `;

    let strokeDashoffset = 615; // Start with the maximum stroke-dashoffset
    let currentSpeed = 0; // Start with the speed at 0 km/h

    let interval; // Variable to hold the interval reference

    // Function to update speed display and SVG animation
    function updateSpeedDisplay() {
        if (currentSpeed < actual_speed) {
            currentSpeed += 1; // Adjust the increment value to control the speed of the transition
            if (currentSpeed > actual_speed) currentSpeed = actual_speed; // Ensure it doesn't exceed the actual speed
        }

        // Update speed display
        let speedDisplay = document.getElementById("speed");
        if (speedDisplay) {
            speedDisplay.textContent = currentSpeed;
        }

        // Update SVG animation
        strokeDashoffset = calculateStrokeDashoffset(currentSpeed);
        let svgElement = document.getElementById("svgElement");
        if (svgElement) {
            svgElement.style.strokeDashoffset = strokeDashoffset;
        }
    }

    // Start the animation when the component is mounted
    onMount(() => {
        // Call updateSpeedDisplay function every animation frame (using tick)
        interval = setInterval(updateSpeedDisplay, 10);

        // Stop the animation once the desired speed is reached
        onDestroy(() => {
            clearInterval(interval);
        });
    });
</script>

